INSERT INTO `cms_article_detail` VALUES (642822672052293, 440515705974853, '<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; font-family: &quot;Open Sans&quot;, sans-serif; text-rendering: optimizelegibility; line-height: 30px; color: rgb(85, 85, 85); text-wrap-mode: wrap; background-color: rgb(255, 255, 255); -webkit-font-smoothing: antialiased !important;\"><span style=\"box-sizing: border-box; color: rgba(0, 0, 0, 0.9); font-family: arial, sans-serif; text-wrap-style: initial;\">&nbsp;&nbsp;</span><span style=\"box-sizing: border-box; color: rgba(0, 0, 0, 0.9); font-family: arial, sans-serif;\">汉字是中华文化的瑰宝，是中华民族智慧的结晶。汉字与普通话相辅相成，是中华民族文化传承与交流的两大基石。汉字为普通话提供了丰富的词汇和表达方式，而普通话则让汉字的传播更加广泛和深入。因此，推广书法也是第27届全国推广普通话宣传周的重要组成部分。贵阳人文科技学院于2024年9月20日上午9：00-11：00举办了一场精彩纷呈的书法比赛。众多书法爱好者齐聚一堂，以笔为媒，展现了中国书法艺术的独特魅力。本次书法比赛邀请了贵阳人文科技学院秘书科副科长汤雷老师、文学与新闻传播学院组织员蔡国兴老师、体育与艺术学院专职教师程文君老师担任评委。</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; font-family: &quot;Open Sans&quot;, sans-serif; text-rendering: optimizelegibility; line-height: 30px; color: rgb(85, 85, 85); text-wrap-mode: wrap; background-color: rgb(255, 255, 255); -webkit-font-smoothing: antialiased !important;\"><span style=\"box-sizing: border-box; color: rgba(0, 0, 0, 0.9); font-family: arial, sans-serif;\"></span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; font-family: &quot;Open Sans&quot;, sans-serif; text-rendering: optimizelegibility; line-height: 30px; color: rgb(85, 85, 85); text-wrap-mode: wrap; background-color: rgb(255, 255, 255); text-align: center; -webkit-font-smoothing: antialiased !important;\"><img src=\"iurl://resources/image/2025/02/09/642822936010821.jpg?type=resource&id=642822936010821&st=Local&sid=440515705974853\" style=\"box-sizing: border-box; vertical-align: middle; border-style: none; height: auto;\"/></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; font-family: &quot;Open Sans&quot;, sans-serif; text-rendering: optimizelegibility; line-height: 30px; color: rgb(85, 85, 85); text-wrap-mode: wrap; background-color: rgb(255, 255, 255); text-align: center; -webkit-font-smoothing: antialiased !important;\"><img src=\"iurl://resources/image/2025/02/09/642822936854597.jpg?type=resource&id=642822936854597&st=Local&sid=440515705974853\" style=\"box-sizing: border-box; vertical-align: middle; border-style: none; height: auto;\"/></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; font-family: &quot;Open Sans&quot;, sans-serif; text-rendering: optimizelegibility; line-height: 30px; color: rgb(85, 85, 85); text-wrap-mode: wrap; background-color: rgb(255, 255, 255); text-align: center; -webkit-font-smoothing: antialiased !important;\"><span style=\"box-sizing: border-box; font-weight: bolder;\">比赛现场</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; font-family: &quot;Open Sans&quot;, sans-serif; text-rendering: optimizelegibility; line-height: 30px; color: rgb(85, 85, 85); text-wrap-mode: wrap; background-color: rgb(255, 255, 255); -webkit-font-smoothing: antialiased !important;\"><span style=\"box-sizing: border-box; color: rgba(0, 0, 0, 0.9); font-family: arial, sans-serif; text-wrap-style: initial;\">&nbsp; &nbsp; &nbsp; &nbsp;比赛内设两个赛道，软笔比赛地点在师德楼305会议室，硬笔在师德楼307会议室。参赛学生需在规定的时间内完成作品。经过激烈的角逐，评委们从内容、字形结构、笔画等方面进行了认真评审，最终评选出一等奖1名、二等奖2名、三等奖3名、优秀奖5名。</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; font-family: &quot;Open Sans&quot;, sans-serif; text-rendering: optimizelegibility; line-height: 30px; color: rgb(85, 85, 85); text-wrap-mode: wrap; background-color: rgb(255, 255, 255); text-align: center; -webkit-font-smoothing: antialiased !important;\"><span style=\"box-sizing: border-box; color: rgba(0, 0, 0, 0.9); font-family: arial, sans-serif; text-wrap-style: initial;\"><img src=\"iurl://resources/image/2025/02/09/642822937092165.jpg?type=resource&id=642822937092165&st=Local&sid=440515705974853\" alt=\"3.jpg\" style=\"box-sizing: border-box; vertical-align: middle; border-style: none; height: auto;\"/></span></p><p><br/></p>', NULL, NULL, 'Y');

